 * @return WP_Term|false ターム。タームが見つからない場合は false を返します。\n */\nfunction my_get_term_child( $terms, $term_id, $depth ) {\n	foreach ( $terms as $term ) {\n		$ancestors = get_ancestors( $term-&gt;term_id, $term-&gt;taxonomy );\n		if ( count( $ancestors ) === $depth ) {\n			if ( $term_id === end( $ancestors ) ) {\n				return $term;\n			}\n		}\n	}\n	return false;\n}\n\n$terms = wp_get_object_terms( $post-&gt;ID, &#039;category&#039; );\nif ( ! empty( $terms ) &amp;&amp; ! is_wp_error( $terms ) ) {\n	if ( $term = my_get_term_child( $terms, 123, 2 ) ) {\n		echo &#039;&lt;p&gt;場所: &lt;a href=&quot;&#039; . esc_url( get_term_link( $term ) ) . &#039;&quot;&gt;&#039; . $term-&gt;name . &#039;&lt;/a&gt;&lt;p&gt;&#039;;\n	}\n	if ( $term = my_get_term_child( $terms, 456, 1 ) ) {\n		echo &#039;&lt;p&gt;構造: &lt;a href=&quot;&#039; . esc_url( get_term_link( $term ) ) . &#039;&quot;&gt;&#039; . $term-&gt;name . &#039;&lt;/a&gt;&lt;p&gt;&#039;;\n	}\n}</code></pre>\n<p>※ コード内の 123 は場所タームの ID、456 は構造タームの ID です。</p>\n						\n					\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:8:\"ishitaka\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:4;a:6:{s:4:\"data\";s:55:\"\n					\n					\n					\n					\n					\n\n					\n\n					\n					\n				\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:252:\"https://ja.wordpress.org/support/topic/%e7%89%b9%e5%ae%9a%e3%81%ae%e8%a6%aa%e3%82%bf%e3%83%bc%e3%83%a0%e3%81%ae%e5%ad%90%e3%81%be%e3%81%9f%e3%81%af%e5%ad%ab%e3%82%bf%e3%83%bc%e3%83%a0%e3%82%92%e8%a1%a8%e7%a4%ba%e3%81%95%e3%81%9b%e3%81%9f/#post-11678385\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:5